#include <bits/stdc++.h>
using namespace std;

class SpamChecker
{
public:
	string spamCheck(string judgeLog, int good, int bad);
};

string SpamChecker::spamCheck (string judgeLog, int good, int bad)
{
    int curScore = 0;
    for(auto it = (judgeLog).begin(); it != (judgeLog).end(); ++it) {
        if(*it == 'o') {
            curScore += good;
        }
        else
            curScore -= bad;
        if(curScore < 0)
            return "SPAM";
    }
	return "NOT SPAM";
}

